// ! 12
import React, { useEffect } from 'react'
import styled from 'styled-components';
import { InnerLayout } from '../../styles/Layout';
import { useGlobalContext } from '../../context/globalContext';
import Form from '../Form/Form';
import IncomeItem from '../IncomeItem/IncomeItem';

function Income() {

    // importing the functions from useGlobalContext.js
    const {addIncome, incomes, getIncomes, deleteIncome, theTotalIncome} = useGlobalContext()

    useEffect(() => {
        getIncomes()
    }, [])
    
    return (  
        <IncomeStyled>
            <InnerLayout>
                <h1>Incomes</h1>

                {/* total income's value passed here */}
                <h2 className="total-income">Total Income: <span>${theTotalIncome}</span></h2>

                <div className="income-content">

                    <div className="form-container">
                        <Form />
                    </div>

                    <div className="incomes">
                        {incomes.map((income) => {
                            const {_id, title, amount, date, category, description} = income;
                            return <IncomeItem 
                                key={_id}
                                id={_id}
                                title={title}
                                description={description}
                                amount={amount} date={date}
                                category={category}
                                indicatorColor="var(--color-green)"
                                deleteItem={deleteIncome}
                            />
                        })}
                    </div>

                </div>

            </InnerLayout>
        </IncomeStyled>
    )
}

const IncomeStyled = styled.div`
    display: flex;
    overflow: auto;

    .total-income {
        display: flex;
        justify-content: center;
        align-items: center;
        background: #FCF6F9;
        border: 2px solid #FFFFFF;
        box-shadow: ;
    }

    .income-content {
        display: flex;
        gap: 2rem;

        .incomes {
            flex: 1; /* to fill the remaining space */
        }
    }
`;

export default Income
