// ! 10
import React, { useEffect } from 'react'
import styled from 'styled-components';
import { InnerLayout } from '../../styles/Layout';
import Chart from '../Chart/Chart';
import { dollars } from '../../utils/Icons'
import { useGlobalContext } from '../../context/globalContext';
import History from '../History/History';

function Dashboard() {

  // We want to display these two
  const {totalExpenses, theTotalIncome, totalBalance, getIncomes, getExpenses} = useGlobalContext()

  // fetch data
  useEffect(() => {
    getIncomes()
    getExpenses()
  },[])
  
  return (
    <DashboardStyled>
        <InnerLayout>
            <h1>All Transactions</h1>

            <div className="stats-con">
              <div className="chart-cont">
                  <Chart />

                  <div className="amount-con">
                    <div className="income">
                      <h2>Total Income</h2>
                      <p>
                        {dollars} {totalExpenses()}
                      </p>
                    </div>
                    <div className="expense">
                    <h2>Total Expense</h2>
                      <p>
                        {dollars} {theTotalIncome()}
                      </p>
                    </div>
                  </div>

                  <div className="balance">
                    <h2>Total Balance</h2>
                    <p>
                        {dollars} {totalBalance()}
                      </p>
                    </div>
              </div>

              <div className="history-con">
                <History />

                <h2 className="salary-title"></h2>
                <div className="salary-item">
                  <p>

                  </p>
                </div>
              </div>

              </div>

        </InnerLayout>
    </DashboardStyled>
  )
}

const DashboardStyled = styled.div`

`;

export default Dashboard
